import pandas as pddf = pd.read_excel('2024普通高校选考科目.xlsx')df.选考科目要求 = df.选考科目要求.map(lambda t: t if t=='不提科目要求' else t[:t.find('(')])# print(df)cond_level = df.层次=='本科'print('省份数量：', len(df.省份.unique()))print('学校数量：', len(df.学校名称.unique()))print('本科学校数量：',      len(df[cond_level]['学校名称'].unique()))print('本科专业数量：',      df[cond_level]['专业（类）名称'].unique().size)print('专科学校数量：',      len(df[~cond_level]['学校名称'].unique()))print('专科专业数量：',      df[~cond_level]['专业（类）名称'].unique().size)print('同时进行本科和专科招生的学校数量：',      len(set(df[cond_level]['学校名称'].unique()) &          set(df[~cond_level]['学校名称'].unique())))print('选考科目要求与数量：')for subject in sorted(df.选考科目要求.unique()):    print(subject, '='*4, sep='', end='>')    print('本科专业数量：',          len(df[cond_level & (df.选考科目要求==subject)]), sep='', end='，')    print('专科专业数量：',          len(df[(~cond_level) & (df.选考科目要求==subject)]), sep='')